void XOR_dec(char* data, size_t data_len, char* key, size_t key_len) {

	int k;

	k = 0;
	for (int i = 0; i < data_len; i++) {
		if (k == key_len - 1) k = 0;

		data[i] = data[i] ^ key[k];
		k++;
	}

	
}


